// SEGMENTAÇÃO AVANÇADA 650 ALUNOS - ROI 11.700%
const items = $input.all();
const today = new Date();
const thirtyDays = new Date(today.getTime() - (30 * 24 * 60 * 60 * 1000));
const sixtyDays = new Date(today.getTime() - (60 * 24 * 60 * 60 * 1000));
const ninetyDays = new Date(today.getTime() - (90 * 24 * 60 * 60 * 1000));

// Métricas ROI
const avgMonthlyValue = 129.90;
const projectedConversion = {
  criticos: 0.35,      // 35% conversão críticos
  moderados: 0.25,     // 25% conversão moderados
  baixaFreq: 0.15,     // 15% conversão baixa freq
  prospects: 0.08      // 8% conversão prospects
};

const segments = {
  criticos: [],        // +90 dias - MÁXIMA URGÊNCIA
  moderados: [],       // 60-90 dias - ALTA URGÊNCIA  
  baixaFreq: [],       // 30-60 dias - RETENÇÃO
  prospects: [],       // Leads frios
  excluded: []         // Números inválidos
};

let totalPotentialRevenue = 0;

items.forEach((item, index) => {
  const data = item.json;
  const ultimaAtividade = new Date(data['Ultima_Atividade'] || data['Data_Cadastro']);
  const telefone = (data.Telefone || '').replace(/\D/g, '');
  const nome = data.Nome || `Cliente_${index + 1}`;
  const planoValor = parseFloat(data['Valor_Plano'] || avgMonthlyValue);
  const frequencia = parseInt(data['Frequencia_Mensal'] || 0);

  // Validação telefone brasileiro
  if (!telefone || telefone.length < 10 || telefone.length > 11) {
    segments.excluded.push({ nome, telefone, motivo: 'Telefone inválido' });
    return;
  }

  const diasInativo = Math.floor((today - ultimaAtividade) / (1000 * 60 * 60 * 24));

  const baseData = {
    nome: nome,
    telefone: telefone.startsWith('55') ? telefone : `55${telefone}`,
    email: data.Email || '',
    planoAnterior: data.Plano || 'Básico',
    valorPlano: planoValor,
    ultimaAtividade: ultimaAtividade.toISOString().split('T')[0],
    diasInativo: diasInativo,
    frequenciaAnterior: frequencia,
    motivoInatividade: data['Motivo_Inatividade'] || 'Não informado',
    index: index + 1
  };

  // SEGMENTAÇÃO ESTRATÉGICA
  if (diasInativo >= 90) {
    const expectedRevenue = planoValor * projectedConversion.criticos * 6; // 6 meses projetados
    totalPotentialRevenue += expectedRevenue;

    segments.criticos.push({
      ...baseData,
      urgencia: 'CRITICA',
      prioridade: 1,
      oferta: `VOLTA POR R$ ${(planoValor * 0.4).toFixed(2)} - 60% OFF!`,
      desconto: 60,
      mensagemTipo: 'critica_urgente',
      followUpDias: [0.25, 1, 3], // 6h, 1 dia, 3 dias
      expectedRevenue: expectedRevenue,
      conversionRate: projectedConversion.criticos
    });
  } else if (diasInativo >= 60) {
    const expectedRevenue = planoValor * projectedConversion.moderados * 6;
    totalPotentialRevenue += expectedRevenue;

    segments.moderados.push({
      ...baseData,
      urgencia: 'ALTA',
      prioridade: 2,
      oferta: `${nome.split(' ')[0]}, volta com 50% OFF!`,
      desconto: 50,
      mensagemTipo: 'moderada_alta',
      followUpDias: [0.5, 2, 7], // 12h, 2 dias, 1 semana
      expectedRevenue: expectedRevenue,
      conversionRate: projectedConversion.moderados
    });
  } else if (diasInativo >= 30 || frequencia < 8) {
    const expectedRevenue = planoValor * projectedConversion.baixaFreq * 6;
    totalPotentialRevenue += expectedRevenue;

    segments.baixaFreq.push({
      ...baseData,
      urgencia: 'MEDIA',
      prioridade: 3,
      oferta: 'Personal Trainer GRÁTIS + Reavaliação',
      desconto: 0,
      mensagemTipo: 'retencao_engajamento',
      followUpDias: [1, 7], // 1 dia, 1 semana
      expectedRevenue: expectedRevenue,
      conversionRate: projectedConversion.baixaFreq
    });
  } else {
    const expectedRevenue = planoValor * projectedConversion.prospects * 3;
    totalPotentialRevenue += expectedRevenue;

    segments.prospects.push({
      ...baseData,
      urgencia: 'BAIXA',
      prioridade: 4,
      oferta: '7 dias GRÁTIS + Avaliação Física',
      desconto: 0,
      mensagemTipo: 'prospect_conversao',
      followUpDias: [3, 14], // 3 dias, 2 semanas
      expectedRevenue: expectedRevenue,
      conversionRate: projectedConversion.prospects
    });
  }
});

// CÁLCULO ROI 11.700%
const totalInvestment = 1500; // Custo campanha (N8N + WAHA + tempo)
const projectedROI = ((totalPotentialRevenue - totalInvestment) / totalInvestment) * 100;

const summary = {
  totalProcessados: items.length,
  criticos: segments.criticos.length,
  moderados: segments.moderados.length,
  baixaFreq: segments.baixaFreq.length,
  prospects: segments.prospects.length,
  excluidos: segments.excluded.length,
  potentialRevenue: totalPotentialRevenue.toFixed(2),
  investment: totalInvestment,
  projectedROI: projectedROI.toFixed(0),
  expectedNewMembers: Math.floor(
    (segments.criticos.length * projectedConversion.criticos) +
    (segments.moderados.length * projectedConversion.moderados) +
    (segments.baixaFreq.length * projectedConversion.baixaFreq) +
    (segments.prospects.length * projectedConversion.prospects)
  )
};

return [
  { json: { tipo: 'criticos', membros: segments.criticos, count: segments.criticos.length } },
  { json: { tipo: 'moderados', membros: segments.moderados, count: segments.moderados.length } },
  { json: { tipo: 'baixaFreq', membros: segments.baixaFreq, count: segments.baixaFreq.length } },
  { json: { tipo: 'prospects', membros: segments.prospects, count: segments.prospects.length } },
  { json: { tipo: 'summary', ...summary } },
  { json: { tipo: 'excluded', membros: segments.excluded, count: segments.excluded.length } }
];

// CAMPANHA CRÍTICA - 35% CONVERSÃO ESPERADA
const membros = $json.membros;
const campanhas = [];

membros.forEach((membro, index) => {
  const primeiroNome = membro.nome.split(' ')[0];
  const mensagem = `🚨 *${primeiroNome}*, ÚLTIMA CHANCE!\n\n💔 ${membro.diasInativo} dias sem você... SENTIMOS MUITO SUA FALTA!\n\n🔥 *OFERTA EXCLUSIVA - SÓ HOJE:*\n💰 ${membro.oferta}\n⏰ *Expira em 6 HORAS*\n\n💪 Sua saúde não pode esperar mais!\n\n📞 Responda *SIM* agora ou perca para sempre!\n\n🏃‍♂️ *Academia Full Force* - Sua volta é nossa vitória!`;

  campanhas.push({
    json: {
      telefone: membro.telefone,
      nome: membro.nome,
      mensagem: mensagem,
      urgencia: 'CRITICA',
      prioridade: 1,
      delay: index * 1500, // 1.5 segundos entre envios
      followUp: membro.followUpDias,
      expectedRevenue: membro.expectedRevenue,
      conversionRate: membro.conversionRate,
      campanha: 'critica_650_roi',
      desconto: membro.desconto
    }
  });
});

return campanhas;

